import React, { useState, useEffect } from "react";
import axios from "axios";
import Navbar from "../Navbar";

const CustomerDashboard = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);
  const [menu, setMenu] = useState([]);
  const [cart, setCart] = useState([]);
  const [orders, setOrders] = useState([]);
  const [subscriptions, setSubscriptions] = useState([]);
  const [showSubscriptionModal, setShowSubscriptionModal] = useState(false);
  const [selectedMenuItem, setSelectedMenuItem] = useState(null);
  const [subscriptionType, setSubscriptionType] = useState("");
  const [subscriptionDates, setSubscriptionDates] = useState({ startDate: "", endDate: "" });

  // ✅ Fetch all restaurants
  useEffect(() => {
    axios.get("http://localhost:5000/api/restaurants")
      .then((res) => setRestaurants(res.data))
      .catch(() => alert("Error fetching restaurants"));
  }, []);

  // ✅ Fetch the menu when a restaurant is selected
  const viewMenu = (restaurantId) => {
    axios.get(`http://localhost:5000/api/restaurants/${restaurantId}/menu`)
      .then((res) => {
        setSelectedRestaurant(restaurantId);
        setMenu(res.data);
      })
      .catch(() => alert("Error fetching menu"));
  };

  // ✅ Add item to cart
  const addToCart = (menuItem) => {
    setCart([...cart, { ...menuItem, quantity: 1 }]);
  };

  // ✅ Remove item from cart
  const removeFromCart = (index) => {
    setCart(cart.filter((_, i) => i !== index));
  };

  // ✅ Handle subscription modal
  const openSubscriptionModal = (menuItem) => {
    setSelectedMenuItem(menuItem);
    setShowSubscriptionModal(true);
  };

  const handleSubscriptionType = (type) => {
    setSubscriptionType(type);
  };

  // ✅ Place a subscription order
  const placeSubscription = () => {
    if (!subscriptionDates.startDate || !subscriptionDates.endDate) {
      alert("Please select a start and end date.");
      return;
    }

    axios.post("http://localhost:5000/api/subscriptions/subscribe", 
      {
        menuItem: selectedMenuItem._id,
        subscriptionType,
        startDate: subscriptionDates.startDate,
        endDate: subscriptionDates.endDate
      },
      { headers: { Authorization: localStorage.getItem("token") } }
    ).then((res) => {
      alert("Subscription added!");
      setSubscriptions([...subscriptions, res.data.subscription]);
      setShowSubscriptionModal(false);
    }).catch(() => alert("Subscription failed"));
  };

  // ✅ Place an order
  const placeOrder = () => {
    if (cart.length === 0) {
      alert("Cart is empty!");
      return;
    }

    const formattedCart = cart.map(item => ({
      menuItem: item._id,
      quantity: item.quantity
    }));

    axios.post(
      "http://localhost:5000/api/orders/place",
      { restaurant: selectedRestaurant, items: formattedCart },
      { headers: { Authorization: localStorage.getItem("token") } }
    ).then((res) => {
      alert("Order placed successfully!");
      setCart([]);
      setOrders([...orders, res.data.order]);
    }).catch(() => alert("Order failed"));
  };

  // ✅ Fetch past orders & subscriptions
  useEffect(() => {
    axios.get("http://localhost:5000/api/orders/my-orders", { headers: { Authorization: localStorage.getItem("token") } })
      .then((res) => setOrders(res.data))
      .catch(() => alert("Error fetching orders"));

    axios.get("http://localhost:5000/api/subscriptions/my-subscriptions ", { headers: { Authorization: localStorage.getItem("token") } })
      .then((res) => setSubscriptions(res.data))
      .catch(() => alert("Error fetching subscriptions"));
  }, []);

  return (
    <div className="bg-gray-100 min-h-screen">
      <Navbar />
      <div className="container mx-auto p-6">
        <h1 className="text-3xl font-bold text-gray-800 mb-6">Customer Dashboard</h1>

        {!selectedRestaurant ? (
          <>
            <h2 className="text-2xl font-semibold text-gray-700">Browse Restaurants</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {restaurants.map((r) => (
                <div key={r._id} className="p-4 bg-white shadow rounded-lg cursor-pointer hover:shadow-md" onClick={() => viewMenu(r._id)}>
                  <h3 className="text-xl font-semibold">{r.name}</h3>
                  <p className="text-gray-600">{r.cuisine}</p>
                </div>
              ))}
            </div>
          </>
        ) : (
          <>
            <button onClick={() => setSelectedRestaurant(null)} className="bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-700 transition mb-4">
              Back to Restaurants
            </button>

            <h2 className="text-2xl font-semibold text-gray-700">Menu</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {menu.map((m) => (
                <div key={m._id} className="p-4 bg-white shadow rounded-lg">
                  <h3 className="text-lg font-semibold">{m.name} - ₹{m.price}</h3>
                  <p className="text-gray-600">{m.description}</p>
                  <button onClick={() => addToCart(m)} className="bg-blue-500 text-white px-4 py-2 mt-2 rounded-md hover:bg-blue-700 transition">
                    Add to Cart
                  </button>
                  <button onClick={() => openSubscriptionModal(m)} className="bg-purple-500 text-white px-4 py-2 mt-2 ml-2 rounded-md hover:bg-purple-700 transition">
                    Subscription
                  </button>
                </div>
              ))}
            </div>
          </>
        )}

        {/* Subscription Modal */}
        {showSubscriptionModal && (
          <div className="fixed inset-0 flex items-center justify-center bg-gray-900 bg-opacity-50">
            <div className="bg-white p-6 rounded-lg">
              <h2 className="text-2xl font-semibold">Choose Subscription Type</h2>
              <button onClick={() => handleSubscriptionType("Monthly")} className="bg-green-500 text-white px-4 py-2 mt-2 rounded-md">Monthly</button>
              <button onClick={() => handleSubscriptionType("Weekly")} className="bg-yellow-500 text-white px-4 py-2 mt-2 ml-2 rounded-md">Weekly</button>
              
              {subscriptionType && (
                <>
                  <input type="date" className="border p-2 mt-2" onChange={(e) => setSubscriptionDates({ ...subscriptionDates, startDate: e.target.value })} />
                  <input type="date" className="border p-2 mt-2 ml-2" onChange={(e) => setSubscriptionDates({ ...subscriptionDates, endDate: e.target.value })} />
                  <button onClick={placeSubscription} className="bg-blue-500 text-white px-4 py-2 mt-2 rounded-md">Confirm Subscription</button>
                </>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default CustomerDashboard;
