import React, { useState, useEffect } from "react";
import axios from "axios";

const RestaurantDashboard = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/orders/restaurant-orders", { headers: { Authorization: localStorage.getItem("token") } })
      .then((res) => setOrders(res.data));
  }, []);

  const updateStatus = (orderId, status) => {
    axios.post(
      `http://localhost:5000/api/orders/update`,
      { orderId, status },
      { headers: { Authorization: localStorage.getItem("token") } }
    ).then(() => {
      setOrders(orders.map(order => order._id === orderId ? { ...order, status } : order));
    });
  };

  return (
    <div>
      <h1>Restaurant Dashboard</h1>
      {orders.map((order) => (
        <div key={order._id}>
          <h3>Order from {order.customer.name}</h3>
          <p>Status: {order.status}</p>
          <button onClick={() => updateStatus(order._id, "Preparing")}>Preparing</button>
          <button onClick={() => updateStatus(order._id, "Out for Delivery")}>Out for Delivery</button>
          <button onClick={() => updateStatus(order._id, "Delivered")}>Delivered</button>
        </div>
      ))}
    </div>
  );
};

export default RestaurantDashboard;
