import React, { useState, useEffect } from "react";
import axios from "axios";
import Navbar from "../Navbar";

const RestaurantDashboard = () => {
  const [orders, setOrders] = useState([]);
  const [restaurantData, setRestaurantData] = useState({ name: "", address: "", cuisine: "" });
  const [restaurants, setRestaurants] = useState([]);
  const [menuItems, setMenuItems] = useState([]);
  const [menuData, setMenuData] = useState({ name: "", description: "", price: "" });

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/orders/restaurant-orders", { headers: { Authorization: localStorage.getItem("token") } })
      .then((res) => setOrders(res.data))
      .catch(() => alert("Error fetching orders"));

    axios
      .get("http://localhost:5000/api/restaurants")
      .then((res) => setRestaurants(res.data))
      .catch(() => alert("Error fetching restaurants"));
  }, []);
    const viewMenu = (restaurantId) => {
      setSelectedRestaurant(restaurantId);
      axios.get(`http://localhost:5000/api/restaurants/${restaurantId}/menu`)
        .then((res) => setMenuItems(res.data))
        .catch(() => alert("Error fetching menu items"));
    };

  const handleChange = (e) => {
    setRestaurantData({ ...restaurantData, [e.target.name]: e.target.value });
  };

  const addRestaurant = (e) => {
    e.preventDefault();
    axios.post(
      "http://localhost:5000/api/restaurants/add",
      restaurantData,
      { headers: { Authorization: localStorage.getItem("token") } }
    ).then((res) => {
      alert(res.data.message);
      setRestaurants([...restaurants, res.data.restaurant]);
      setRestaurantData({ name: "", address: "", cuisine: "" });
    }).catch(() => alert("Error adding restaurant"));
  };

  const updateStatus = (orderId, status) => {
    axios.post(
      "http://localhost:5000/api/orders/update",
      { orderId, status },
      { headers: { Authorization: localStorage.getItem("token") } }
    ).then(() => {
      setOrders(orders.map(order => order._id === orderId ? { ...order, status } : order));
    }).catch(() => alert("Error updating status"));
  };

  return (
    <div className="bg-gray-100 min-h-screen">
      <Navbar />
      <div className="container mx-auto p-6">
        <h1 className="text-3xl font-bold text-gray-800 mb-6">Restaurant Admin Dashboard</h1>

        {/* Add New Restaurant Form */}
        <h2 className="text-2xl font-semibold text-gray-700">Add a New Restaurant</h2>
        <form onSubmit={addRestaurant} className="bg-white p-6 rounded-lg shadow-md space-y-4">
          <input type="text" name="name" placeholder="Restaurant Name" value={restaurantData.name} onChange={handleChange} required 
            className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <input type="text" name="address" placeholder="Address" value={restaurantData.address} onChange={handleChange} required 
            className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <input type="text" name="cuisine" placeholder="Cuisine Type" value={restaurantData.cuisine} onChange={handleChange} required 
            className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-700 transition">
            Add Restaurant
          </button>
        </form>

        {/* Orders Management */}
        <h2 className="text-2xl font-semibold text-gray-700 mt-6">Manage Orders</h2>
        {orders.length === 0 ? <p>No orders yet</p> : (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {orders.map((order) => (
              <div key={order._id} className="bg-white p-4 rounded-lg shadow-md">
                <h3 className="text-lg font-semibold">Order from {order.customer.name}</h3>
                <p className="text-gray-600">Status: {order.status}</p>
                <div className="flex space-x-2 mt-2">
                  <button onClick={() => updateStatus(order._id, "Preparing")} className="bg-yellow-500 text-white px-4 py-2 rounded-md hover:bg-yellow-700 transition">
                    Preparing
                  </button>
                  <button onClick={() => updateStatus(order._id, "Out for Delivery")} className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-700 transition">
                    Out for Delivery
                  </button>
                  <button onClick={() => updateStatus(order._id, "Delivered")} className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-700 transition">
                    Delivered
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default RestaurantDashboard;
