import React, { useState, useEffect } from "react";
import axios from "axios";
import Navbar from "../Navbar";
import { format } from 'date-fns';

const CustomerDashboard = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);
  const [menu, setMenu] = useState([]);
  const [cart, setCart] = useState([]);
  const [orders, setOrders] = useState([]);
  const [subscriptions, setSubscriptions] = useState([]);
  const [showSubscriptionModal, setShowSubscriptionModal] = useState(false);
  const [selectedMenuItem, setSelectedMenuItem] = useState(null);
  const [subscriptionType, setSubscriptionType] = useState("");
  const [subscriptionDates, setSubscriptionDates] = useState({ startDate: "", endDate: "" });
  const [loading, setLoading] = useState({
    restaurants: false,
    menu: false,
    orders: false,
    subscriptions: false
  });

  // âœ… Fetch all restaurants
  useEffect(() => {
    axios.get("http://localhost:5000/api/restaurants")
      .then((res) => setRestaurants(res.data))
      .catch(() => alert("Error fetching restaurants"));
  }, []);

  // âœ… Fetch the menu when a restaurant is selected
  const viewMenu = (restaurantId) => {
    axios.get(`http://localhost:5000/api/restaurants/${restaurantId}/menu`)
      .then((res) => {
        setSelectedRestaurant(restaurantId);
        setMenu(res.data);
      })
      .catch(() => alert("Error fetching menu"));
  };

// Cart operations
const addToCart = (menuItem) => {
  const existingItem = cart.find(item => item._id === menuItem._id);
  if (existingItem) {
    setCart(cart.map(item => 
      item._id === menuItem._id 
        ? {...item, quantity: item.quantity + 1} 
        : item
    ));
  } else {
    setCart([...cart, {...menuItem, quantity: 1}]);
  }
};

const updateQuantity = (id, newQuantity) => {
  if (newQuantity < 1) return;
  setCart(cart.map(item => 
    item._id === id ? {...item, quantity: newQuantity} : item
  ));
};

const removeFromCart = (id) => {
  setCart(cart.filter(item => item._id !== id));
};

  // âœ… Handle subscription modal
  const openSubscriptionModal = (menuItem) => {
    setSelectedMenuItem(menuItem);
    setShowSubscriptionModal(true);
  };

  const handleSubscriptionType = (type) => {
    setSubscriptionType(type);
  };

  // âœ… Place a subscription order
  const placeSubscription = () => {
    if (!subscriptionDates.startDate || !subscriptionDates.endDate) {
      alert("Please select a start and end date.");
      return;
    }

    axios.post("http://localhost:5000/api/subscriptions/subscribe", 
      {
        menuItem: selectedMenuItem._id,
        subscriptionType,
        startDate: subscriptionDates.startDate,
        endDate: subscriptionDates.endDate
      },
      { headers: { Authorization: localStorage.getItem("token") } }
    ).then((res) => {
      alert("Subscription added!");
      setSubscriptions([...subscriptions, res.data.subscription]);
      setShowSubscriptionModal(false);
    }).catch(() => alert("Subscription failed"));
  };

  // âœ… Place an order
  const placeOrder = () => {
    if (cart.length === 0) {
      alert("Cart is empty!");
      return;
    }

    const formattedCart = cart.map(item => ({
      menuItem: item._id,
      quantity: item.quantity
    }));

    axios.post(
      "http://localhost:5000/api/orders/place",
      { restaurant: selectedRestaurant, items: formattedCart },
      { headers: { Authorization: localStorage.getItem("token") } }
    ).then((res) => {
      alert("Order placed successfully!");
      setCart([]);
      setOrders([...orders, res.data.order]);
    }).catch(() => alert("Order failed"));
  };

  // âœ… Fetch past orders & subscriptions
  useEffect(() => {
    axios.get("http://localhost:5000/api/orders/my-orders", { headers: { Authorization: localStorage.getItem("token") } })
      .then((res) => setOrders(res.data))
      .catch(() => alert("Error fetching orders"));

    axios.get("http://localhost:5000/api/subscriptions/my-subscriptions", { headers: { Authorization: localStorage.getItem("token") } })
      .then((res) => setSubscriptions(res.data))
      .catch(() => alert("Error fetching subscriptions"));
  }, []);


   // Calculate cart total
   const cartTotal = cart.reduce((total, item) => total + (item.price * item.quantity), 0);

  return (
    <div className="bg-gray-100 min-h-screen">
      <Navbar />
      <div className="container mx-auto p-6">
        <h1 className="text-3xl font-bold text-gray-800 mb-6">Customer Dashboard</h1>

        {!selectedRestaurant ? (
          <>
            <h2 className="text-2xl font-semibold text-gray-700">Browse Restaurants</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {restaurants.map((r) => (
                <div key={r._id} className="p-4 bg-white shadow rounded-lg cursor-pointer hover:shadow-md" onClick={() => viewMenu(r._id)}>
                  <h3 className="text-xl font-semibold">{r.name}</h3>
                  <p className="text-gray-600">{r.cuisine}</p>
                </div>
              ))}
            </div>
          </>
        ) : (
          <>
            <button onClick={() => setSelectedRestaurant(null)} className="bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-700 transition mb-4">
              Back to Restaurants
            </button>

            <h2 className="text-2xl font-semibold text-gray-700">Menu</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {menu.map((m) => (
                <div key={m._id} className="p-4 bg-white shadow rounded-lg">
                  <h3 className="text-lg font-semibold">{m.name} - â‚¹{m.price}</h3>
                  <p className="text-gray-600">{m.description}</p>
                  <button onClick={() => addToCart(m)} className="bg-blue-500 text-white px-4 py-2 mt-2 rounded-md hover:bg-blue-700 transition">
                    Add to Cart
                  </button>
                  <button onClick={() => openSubscriptionModal(m)} className="bg-purple-500 text-white px-4 py-2 mt-2 ml-2 rounded-md hover:bg-purple-700 transition">
                    Subscription
                  </button>
                </div>
              ))}
            </div>
          </>
        )}
        {/* ðŸ›’ Cart */}
        {/* Shopping Cart */}
        <section className="mb-8">
          <h2 className="text-2xl font-semibold text-gray-700 mb-4">Your Cart</h2>
          {cart.length === 0 ? (
            <div className="bg-white p-6 rounded-lg shadow-sm text-center">
              <p className="text-gray-500">Your cart is empty</p>
            </div>
          ) : (
            <div className="bg-white rounded-lg shadow-md overflow-hidden">
              <div className="divide-y divide-gray-200">
                {cart.map((item) => (
                  <div key={item._id} className="p-4 flex justify-between items-center">
                    <div className="flex-1">
                      <h4 className="font-medium text-gray-800">{item.name}</h4>
                      <p className="text-sm text-gray-500">{item.description}</p>
                    </div>
                    <div className="flex items-center space-x-4">
                      <div className="flex items-center border rounded-md">
                        <button
                          onClick={() => updateQuantity(item._id, item.quantity - 1)}
                          className="px-3 py-1 text-gray-600 hover:bg-gray-100"
                        >
                          -
                        </button>
                        <span className="px-2">{item.quantity}</span>
                        <button
                          onClick={() => updateQuantity(item._id, item.quantity + 1)}
                          className="px-3 py-1 text-gray-600 hover:bg-gray-100"
                        >
                          +
                        </button>
                      </div>
                      <span className="font-medium w-20 text-right">
                        â‚¹{(item.price * item.quantity).toFixed(2)}
                      </span>
                      <button
                        onClick={() => removeFromCart(item._id)}
                        className="text-red-500 hover:text-red-700"
                      >
                        <svg className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
                        </svg>
                      </button>
                    </div>
                  </div>
                ))}
              </div>
              <div className="p-4 bg-gray-50 border-t flex justify-between items-center">
                <span className="font-medium">Total:</span>
                <span className="font-bold text-lg">â‚¹{cartTotal.toFixed(2)}</span>
              </div>
              <div className="p-4">
                <button
                  onClick={placeOrder}
                  className="w-full bg-green-600 hover:bg-green-700 text-white py-2 px-4 rounded-md font-medium transition"
                >
                  Place Order
                </button>
              </div>
            </div>
          )}
        </section>

<h2 className="text-2xl font-semibold text-gray-700 mt-6">My Orders</h2>
        {orders.length === 0 ? <p>No orders yet</p> : (
          <table className="w-full bg-white shadow-md rounded-lg mt-4">
            <thead>
              <tr className="bg-gray-200">
                <th className="p-3 text-left">Restaurant</th>
                <th className="p-3 text-left">Items</th>
                <th className="p-3 text-left">Total</th>
                <th className="p-3 text-left">Status</th>
              </tr>
            </thead>
            <tbody>
              {orders.map((order) => (
                <tr key={order._id} className="border-b">
                  <td className="p-3">{order.restaurant?.name || "N/A"}</td>
                  <td className="p-3">
                    {order.items?.map((item) => (
                      <div key={item.menuItem?._id || item._id}>
                        {item.menuItem?.name || "N/A"} x{item.quantity}
                      </div>
                    ))}
                  </td>
                  <td className="p-3">
                    â‚¹{order.items?.reduce((total, item) => total + (item.menuItem?.price || 0) * item.quantity, 0)}
                  </td>
                  <td className="p-3">{order.status || "N/A"}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default CustomerDashboard;
