import React, { useState, useEffect } from "react";
import axios from "axios";
import Navbar from "../Navbar";

const RestaurantDashboard = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);
  const [menuItems, setMenuItems] = useState([]);
  const [menuData, setMenuData] = useState({ name: "", description: "", price: "" });

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/restaurants", {
        headers: { Authorization: localStorage.getItem("token") },
      })
      .then((res) => setRestaurants(res.data))
      .catch(() => alert("Error fetching restaurants"));
  }, []);

  const selectRestaurant = (restaurant) => {
    setSelectedRestaurant(restaurant);
    axios
      .get(`http://localhost:5000/api/menu/${restaurant._id}`)
      .then((res) => setMenuItems(res.data))
      .catch(() => alert("Error fetching menu items"));
  };

  const handleMenuChange = (e) => {
    setMenuData({ ...menuData, [e.target.name]: e.target.value });
  };

  const addMenuItem = (e) => {
    e.preventDefault();
    if (!selectedRestaurant) return alert("Select a restaurant first!");

    axios
      .post(
        "http://localhost:5000/api/menu/add",
        { ...menuData, restaurantId: selectedRestaurant._id },
        { headers: { Authorization: localStorage.getItem("token") } }
      )
      .then((res) => {
        alert("Menu item added!");
        setMenuItems([...menuItems, res.data]);
        setMenuData({ name: "", description: "", price: "" });
      })
      .catch(() => alert("Error adding menu item"));
  };

  return (
    <div className="bg-gray-100 min-h-screen">
      <Navbar />
      <div className="container mx-auto p-6">
        <h1 className="text-3xl font-bold text-gray-800 mb-6">Restaurant Admin Dashboard</h1>

        {/* List of Restaurants */}
        <h2 className="text-2xl font-semibold text-gray-700">Your Restaurants</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-4">
          {restaurants.length === 0 ? <p>No restaurants found</p> : (
            restaurants.map((restaurant) => (
              <div
                key={restaurant._id}
                className={`p-4 rounded-lg shadow-md cursor-pointer ${
                  selectedRestaurant?._id === restaurant._id ? "bg-blue-500 text-white" : "bg-white"
                }`}
                onClick={() => selectRestaurant(restaurant)}
              >
                <h3 className="text-lg font-semibold">{restaurant.name}</h3>
                <p className="text-gray-600">{restaurant.address}</p>
                <p className="text-gray-500">{restaurant.cuisine}</p>
              </div>
            ))
          )}
        </div>

        {/* Add Menu Item */}
        {selectedRestaurant && (
          <>
            <h2 className="text-2xl font-semibold text-gray-700 mt-6">Add Menu for {selectedRestaurant.name}</h2>
            <form onSubmit={addMenuItem} className="bg-white p-6 rounded-lg shadow-md space-y-4">
              <input
                type="text"
                name="name"
                placeholder="Menu Item Name"
                value={menuData.name}
                onChange={handleMenuChange}
                required
                className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
              <input
                type="text"
                name="description"
                placeholder="Description"
                value={menuData.description}
                onChange={handleMenuChange}
                className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
              <input
                type="number"
                name="price"
                placeholder="Price"
                value={menuData.price}
                onChange={handleMenuChange}
                required
                className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
              <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-700 transition">
                Add Menu Item
              </button>
            </form>
          </>
        )}

        {/* Display Menu Items */}
        {selectedRestaurant && menuItems.length > 0 && (
          <>
            <h2 className="text-2xl font-semibold text-gray-700 mt-6">Menu for {selectedRestaurant.name}</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-4">
              {menuItems.map((item) => (
                <div key={item._id} className="bg-white p-4 rounded-lg shadow-md">
                  <h3 className="text-lg font-semibold">{item.name}</h3>
                  <p className="text-gray-600">{item.description}</p>
                  <p className="text-gray-500">Price: ₹{item.price}</p>
                </div>
              ))}
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default RestaurantDashboard;
