import React, { useState, useEffect } from "react";
import axios from "axios";
import Navbar from "../Navbar";

const SuperAdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [restaurants, setRestaurants] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [userOrders, setUserOrders] = useState([]);
  const [userSubscriptions, setUserSubscriptions] = useState([]);
  const [loading, setLoading] = useState({
    users: false,
    restaurants: false,
    orders: false,
    subscriptions: false
  });

  useEffect(() => {
    fetchUsers();
    fetchRestaurants();
  }, []);

  const fetchUsers = async () => {
    setLoading(prev => ({...prev, users: true}));
    try {
      const response = await axios.get("http://localhost:5000/api/superadmin/users", {
        headers: { Authorization: localStorage.getItem("token") }
      });
      setUsers(response.data);
    } catch (error) {
      console.error("Error fetching users:", error);
      alert("Failed to load users");
    } finally {
      setLoading(prev => ({...prev, users: false}));
    }
  };

  const fetchRestaurants = async () => {
    setLoading(prev => ({...prev, restaurants: true}));
    try {
      const response = await axios.get("http://localhost:5000/api/restaurants");
      setRestaurants(response.data);
    } catch (error) {
      console.error("Error fetching restaurants:", error);
      alert("Failed to load restaurants");
    } finally {
      setLoading(prev => ({...prev, restaurants: false}));
    }
  };

  const fetchUserDetails = async (userId) => {
    setLoading(prev => ({...prev, orders: true, subscriptions: true}));
    try {
      const [ordersRes, subsRes] = await Promise.all([
        axios.get(`http://localhost:5000/api/superadmin/orders/${userId}`, {
          headers: { Authorization: localStorage.getItem("token") }
        }),
        axios.get(`http://localhost:5000/api/superadmin/subscriptions/${userId}`, {
          headers: { Authorization: localStorage.getItem("token") }
        })
      ]);
      setUserOrders(ordersRes.data);
      setUserSubscriptions(subsRes.data);
    } catch (error) {
      console.error("Error fetching user details:", error);
      alert("Failed to load user details");
    } finally {
      setLoading(prev => ({...prev, orders: false, subscriptions: false}));
    }
  };

  const handleUserClick = (user) => {
    setSelectedUser(user);
    fetchUserDetails(user._id);
  };

  const deleteUser = async (id) => {
    if (!window.confirm("Are you sure you want to delete this user?")) return;
    
    try {
      await axios.delete(`http://localhost:5000/api/superadmin/users/${id}`, {
        headers: { Authorization: localStorage.getItem("token") }
      });
      setUsers(users.filter(user => user._id !== id));
      if (selectedUser?._id === id) {
        setSelectedUser(null);
        setUserOrders([]);
        setUserSubscriptions([]);
      }
      alert("User deleted successfully");
    } catch (error) {
      console.error("Error deleting user:", error);
      alert("Failed to delete user");
    }
  };

  return (
    <div className="bg-gray-100 min-h-screen">
      <Navbar />
      <div className="container mx-auto p-6">
        <h1 className="text-3xl font-bold text-gray-800 mb-6">Super Admin Dashboard</h1>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Users List */}
          <div className="lg:col-span-1">
            <h2 className="text-2xl font-semibold text-gray-700 mb-4">Users</h2>
            {loading.users ? (
              <div className="flex justify-center items-center h-40">
                <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
              </div>
            ) : (
              <div className="bg-white shadow-md rounded-lg overflow-hidden">
                <table className="w-full">
                  <thead>
                    <tr className="bg-gray-200">
                      <th className="p-3 text-left">Name</th>
                      <th className="p-3 text-left">Role</th>
                      <th className="p-3 text-left">Action</th>
                    </tr>
                  </thead>
                  <tbody>
                    {users.map((user) => (
                      <tr 
                        key={user._id} 
                        className={`cursor-pointer hover:bg-gray-50 ${selectedUser?._id === user._id ? 'bg-blue-50' : ''}`}
                        onClick={() => handleUserClick(user)}
                      >
                        <td className="p-3">{user.name}</td>
                        <td className="p-3">{user.role}</td>
                        <td className="p-3">
                          <button 
                            onClick={(e) => {
                              e.stopPropagation();
                              deleteUser(user._id);
                            }} 
                            className="bg-red-500 text-white px-3 py-1 rounded-md hover:bg-red-700 transition text-sm"
                            disabled={loading.users}
                          >
                            Delete
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </div>

          {/* User Details */}
          {selectedUser && (
            <div className="lg:col-span-2 space-y-6">
              <div className="bg-white p-4 rounded-lg shadow-md">
                <h2 className="text-xl font-semibold mb-2">{selectedUser.name}'s Details</h2>
                <p>Email: {selectedUser.email}</p>
                <p>Role: {selectedUser.role}</p>
                <p>Joined: {new Date(selectedUser.createdAt).toLocaleDateString()}</p>
              </div>

              {/* Orders Section */}
              <div className="bg-white p-4 rounded-lg shadow-md">
                <h2 className="text-xl font-semibold mb-4">Order History</h2>
                {loading.orders ? (
                  <div className="flex justify-center items-center h-40">
                    <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
                  </div>
                ) : userOrders.length > 0 ? (
                  <div className="overflow-x-auto">
                    <table className="w-full">
                      <thead>
                        <tr className="bg-gray-200">
                          <th className="p-2 text-left">Order ID</th>
                          <th className="p-2 text-left">Restaurant</th>
                          <th className="p-2 text-left">Total</th>
                          <th className="p-2 text-left">Status</th>
                        </tr>
                      </thead>
                      <tbody>
                        {userOrders.map((order) => (
                          <tr key={order._id} className="border-b">
                            <td className="p-2">{order._id.substring(0, 8)}...</td>
                            <td className="p-2">{order.restaurant?.name || 'N/A'}</td>
                            <td className="p-2">
                              â‚¹{order.items.reduce((total, item) => total + (item.menuItem?.price || 0) * item.quantity, 0).toFixed(2)}
                            </td>
                            <td className="p-2">
                              <span className={`px-2 py-1 rounded-full text-xs ${
                                order.status === 'Pending' ? 'bg-yellow-100 text-yellow-800' :
                                order.status === 'Delivered' ? 'bg-green-100 text-green-800' :
                                'bg-blue-100 text-blue-800'
                              }`}>
                                {order.status}
                              </span>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                ) : (
                  <p>No orders found</p>
                )}
              </div>

              {/* Subscriptions Section */}
              <div className="bg-white p-4 rounded-lg shadow-md">
                <h2 className="text-xl font-semibold mb-4">Subscriptions</h2>
                {loading.subscriptions ? (
                  <div className="flex justify-center items-center h-40">
                    <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
                  </div>
                ) : userSubscriptions.length > 0 ? (
                  <div className="overflow-x-auto">
                    <table className="w-full">
                      <thead>
                        <tr className="bg-gray-200">
                          <th className="p-2 text-left">Item</th>
                          <th className="p-2 text-left">Type</th>
                          <th className="p-2 text-left">Period</th>
                          <th className="p-2 text-left">Status</th>
                        </tr>
                      </thead>
                      <tbody>
                        {userSubscriptions.map((sub) => {
                          const isActive = new Date(sub.endDate) > new Date();
                          return (
                            <tr key={sub._id} className="border-b">
                              <td className="p-2">{sub.menuItem?.name || 'N/A'}</td>
                              <td className="p-2 capitalize">{sub.subscriptionType}</td>
                              <td className="p-2">
                                {new Date(sub.startDate).toLocaleDateString()} - {new Date(sub.endDate).toLocaleDateString()}
                              </td>
                              <td className="p-2">
                                <span className={`px-2 py-1 rounded-full text-xs ${
                                  isActive ? 'bg-green-100 text-green-800' : 'bg-gray-100 text-gray-800'
                                }`}>
                                  {isActive ? 'Active' : 'Expired'}
                                </span>
                              </td>
                            </tr>
                          );
                        })}
                      </tbody>
                    </table>
                  </div>
                ) : (
                  <p>No subscriptions found</p>
                )}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default SuperAdminDashboard;
