import React, { useState, useEffect } from "react";
import axios from "axios";
import Navbar from "../Navbar";

const CustomerDashboard = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);
  const [menu, setMenu] = useState([]);
  const [cart, setCart] = useState([]);
  const [orders, setOrders] = useState([]);

  // ✅ Fetch all restaurants
  useEffect(() => {
    axios.get("http://localhost:5000/api/restaurants")
      .then((res) => setRestaurants(res.data))
      .catch(() => alert("Error fetching restaurants"));
  }, []);

  // ✅ Fetch the menu when a restaurant is selected
  const viewMenu = (restaurantId) => {
    axios.get(`http://localhost:5000/api/restaurants/${restaurantId}/menu`)
      .then((res) => {
        setSelectedRestaurant(restaurantId);
        setMenu(res.data);
      })
      .catch(() => alert("Error fetching menu"));
  };

  // ✅ Add items to cart
  const addToCart = (menuItem) => {
    setCart([...cart, { ...menuItem, quantity: 1 }]);
  };

  // ✅ Place an order
  const placeOrder = () => {
    if (cart.length === 0) {
      alert("Cart is empty!");
      return;
    }

    const formattedCart = cart.map(item => ({
      menuItem: item._id,  // Send only the menu item ID
      quantity: item.quantity
    }));

    axios.post(
      "http://localhost:5000/api/orders/place",
      { restaurant: selectedRestaurant, items: formattedCart },
      { headers: { Authorization: localStorage.getItem("token") } }
    ).then((res) => {
      alert("Order placed successfully!");
      setCart([]);
      setOrders([...orders, res.data.order]); // Add the new order to the orders list
    }).catch((error) => {
      console.error("Order failed:", error.response?.data || error.message);
      alert("Order failed. Check console for details.");
    });
  };

  // ✅ Fetch past orders
  useEffect(() => {
    axios.get("http://localhost:5000/api/orders/my-orders", {
      headers: { Authorization: localStorage.getItem("token") }
    }).then((res) => setOrders(res.data))
      .catch(() => alert("Error fetching orders"));
  }, []);

  return (
    <div className="bg-gray-100 min-h-screen">
      <Navbar />
      <div className="container mx-auto p-6">
        <h1 className="text-3xl font-bold text-gray-800 mb-6">Customer Dashboard</h1>

        {!selectedRestaurant ? (
          <>
            <h2 className="text-2xl font-semibold text-gray-700">Browse Restaurants</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {restaurants.map((r) => (
                <div key={r._id} className="p-4 bg-white shadow rounded-lg cursor-pointer hover:shadow-md" onClick={() => viewMenu(r._id)}>
                  <h3 className="text-xl font-semibold">{r.name}</h3>
                  <p className="text-gray-600">{r.cuisine}</p>
                </div>
              ))}
            </div>
          </>
        ) : (
          <>
            <button onClick={() => setSelectedRestaurant(null)} className="bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-700 transition mb-4">
              Back to Restaurants
            </button>

            <h2 className="text-2xl font-semibold text-gray-700">Menu</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {menu.map((m) => (
                <div key={m._id} className="p-4 bg-white shadow rounded-lg">
                  <h3 className="text-lg font-semibold">{m.name} - ₹{m.price}</h3>
                  <p className="text-gray-600">{m.description}</p>
                  <button onClick={() => addToCart(m)} className="bg-blue-500 text-white px-4 py-2 mt-2 rounded-md hover:bg-blue-700 transition">
                    Add to Cart
                  </button>
                </div>
              ))}
            </div>

            <h2 className="text-2xl font-semibold text-gray-700 mt-6">Cart</h2>
            {cart.length === 0 ? <p>No items in cart</p> : (
              <div className="p-4 bg-white shadow rounded-lg">
                {cart.map((item, index) => (
                  <p key={index}>{item.name} x {item.quantity}</p>
                ))}
                <button onClick={placeOrder} className="bg-green-500 text-white px-4 py-2 mt-2 rounded-md hover:bg-green-700 transition">
                  Place Order
                </button>
              </div>
            )}
          </>
        )}

        <h2 className="text-2xl font-semibold text-gray-700 mt-6">My Orders</h2>
        {orders.length === 0 ? <p>No orders yet</p> : (
          <table className="w-full bg-white shadow-md rounded-lg mt-4">
            <thead>
              <tr className="bg-gray-200">
                <th className="p-3 text-left">Restaurant</th>
                <th className="p-3 text-left">Items</th>
                <th className="p-3 text-left">Total</th>
                <th className="p-3 text-left">Status</th>
              </tr>
            </thead>
            <tbody>
              {orders.map((order) => (
                <tr key={order._id} className="border-b">
                  <td className="p-3">{order.restaurant?.name || "N/A"}</td>
                  <td className="p-3">
                    {order.items?.map((item) => (
                      <div key={item.menuItem?._id || item._id}>
                        {item.menuItem?.name || "N/A"} x{item.quantity}
                      </div>
                    ))}
                  </td>
                  <td className="p-3">
                    ₹{order.items?.reduce((total, item) => total + (item.menuItem?.price || 0) * item.quantity, 0)}
                  </td>
                  <td className="p-3">{order.status || "N/A"}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default CustomerDashboard;
