import React, { useState, useEffect } from "react";
import axios from "axios";
import Navbar from "../Navbar";

const CustomerDashboard = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);
  const [menu, setMenu] = useState([]);
  const [cart, setCart] = useState([]);
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/api/restaurants").then((res) => setRestaurants(res.data));
  }, []);

  const viewMenu = (restaurantId) => {
    axios.get(`http://localhost:5000/api/menu/${restaurantId}`).then((res) => {
      setSelectedRestaurant(restaurantId);
      setMenu(res.data);
    });
  };

  const addToCart = (menuItem) => {
    setCart([...cart, { ...menuItem, quantity: 1 }]);
  };

  const placeOrder = () => {
    axios.post(
      "http://localhost:5000/api/orders/place",
      { restaurant: selectedRestaurant, items: cart },
      { headers: { Authorization: localStorage.getItem("token") } }
    ).then(() => {
      alert("Order placed!");
      setCart([]);
    });
  };

  useEffect(() => {
    axios.get("http://localhost:5000/api/orders/my-orders", {
      headers: { Authorization: localStorage.getItem("token") },
    }).then((res) => setOrders(res.data));
  }, []);
  

  return (
    <div>
      <h1>Customer Dashboard</h1>

      {!selectedRestaurant ? (
        <>
          <h2>Browse Restaurants</h2>
          {restaurants.map((r) => (
            <div key={r._id} onClick={() => viewMenu(r._id)}>
              <h3>{r.name}</h3>
              <p>{r.cuisine}</p>
            </div>
          ))}
        </>
      ) : (
        <>
          <h2>Menu</h2>
          {menu.map((m) => (
            <div key={m._id}>
              <h3>{m.name} - ₹{m.price}</h3>
              <button onClick={() => addToCart(m)}>Add to Cart</button>
            </div>
          ))}

          <h2>Cart</h2>
          {cart.map((item, index) => (
            <p key={index}>{item.name} x {item.quantity}</p>
          ))}

          <button onClick={placeOrder}>Place Order</button>
        </>
      )}

<div>
      
      <h2>My Orders</h2>
      <table className="table">
        <thead>
          <tr>
            <th>Restaurant</th>
            <th>Items</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {orders.map((order) => (
            <tr key={order._id}>
              <td>{order.restaurant.name}</td>
              <td>{order.items.map((item) => `${item.menuItem.name} x${item.quantity}`).join(", ")}</td>
              <td>{order.status}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
    </div>
  );
};

export default CustomerDashboard;
