import React, { useState, useEffect } from "react";
import axios from "axios";
import Navbar from "../Navbar";

const SuperAdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [restaurants, setRestaurants] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/api/superadminusers", {
      headers: { Authorization: localStorage.getItem("token") },
    }).then((res) => setUsers(res.data));

    axios.get("http://localhost:5000/api/restaurants")
      .then((res) => setRestaurants(res.data))
      .catch(() => alert("Error fetching restaurants"));
  }, []);

  const deleteUser = (id) => {
    axios.delete(`http://localhost:5000/api/users/${id}`, {
      headers: { Authorization: localStorage.getItem("token") },
    }).then(() => setUsers(users.filter((user) => user._id !== id)));
  };

  return (
    <div className="bg-gray-100 min-h-screen">
      <Navbar />
      <div className="container mx-auto p-6">
        <h1 className="text-3xl font-bold text-gray-800 mb-6">Super Admin Dashboard</h1>

        {/* Manage Users */}
        <h2 className="text-2xl font-semibold text-gray-700">Manage Users</h2>
        <table className="w-full bg-white shadow-md rounded-lg mt-4">
          <thead>
            <tr className="bg-gray-200">
              <th className="p-3 text-left">Name</th>
              <th className="p-3 text-left">Role</th>
              <th className="p-3 text-left">Action</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user._id}>
                <td className="p-3">{user.name}</td>
                <td className="p-3">{user.role}</td>
                <td className="p-3">
                  <button onClick={() => deleteUser(user._id)} className="bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-700 transition">
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default SuperAdminDashboard;
