import React, { useState, useEffect } from "react";
import axios from "axios";
import Navbar from "../Navbar";

const CustomerDashboard = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);
  const [menu, setMenu] = useState([]);
  const [cart, setCart] = useState([]);
  const [orders, setOrders] = useState([]);
  const [subscriptions, setSubscriptions] = useState([]);
  const [showSubscriptionModal, setShowSubscriptionModal] = useState(false);
  const [selectedMenuItem, setSelectedMenuItem] = useState(null);
  const [subscriptionType, setSubscriptionType] = useState("");
  const [subscriptionDates, setSubscriptionDates] = useState({ startDate: "", endDate: "" });

  // Fetch all restaurants
  useEffect(() => {
    axios.get("http://localhost:5000/api/restaurants")
      .then((res) => setRestaurants(res.data))
      .catch(() => alert("Error fetching restaurants"));
  }, []);

  // Fetch the menu when a restaurant is selected
  const viewMenu = (restaurantId) => {
    axios.get(`http://localhost:5000/api/restaurants/${restaurantId}/menu`)
      .then((res) => {
        setSelectedRestaurant(restaurantId);
        setMenu(res.data);
      })
      .catch(() => alert("Error fetching menu"));
  };

  // Add item to cart
  const addToCart = (menuItem) => {
    const existingItem = cart.find(item => item._id === menuItem._id);
    if (existingItem) {
      setCart(cart.map(item => 
        item._id === menuItem._id 
          ? { ...item, quantity: item.quantity + 1 } 
          : item
      ));
    } else {
      setCart([...cart, { ...menuItem, quantity: 1 }]);
    }
  };

  // Remove item from cart
  const removeFromCart = (index) => {
    setCart(cart.filter((_, i) => i !== index));
  };

  // Update item quantity in cart
  const updateQuantity = (index, newQuantity) => {
    if (newQuantity < 1) return;
    const updatedCart = [...cart];
    updatedCart[index].quantity = newQuantity;
    setCart(updatedCart);
  };

  // Handle subscription modal
  const openSubscriptionModal = (menuItem) => {
    setSelectedMenuItem(menuItem);
    setShowSubscriptionModal(true);
  };

  const handleSubscriptionType = (type) => {
    setSubscriptionType(type);
  };

  // Place a subscription order
  const placeSubscription = () => {
    if (!subscriptionDates.startDate || !subscriptionDates.endDate) {
      alert("Please select a start and end date.");
      return;
    }

    axios.post("http://localhost:5000/api/subscriptions/subscribe", 
      {
        menuItem: selectedMenuItem._id,
        subscriptionType,
        startDate: subscriptionDates.startDate,
        endDate: subscriptionDates.endDate
      },
      { headers: { Authorization: localStorage.getItem("token") } }
    ).then((res) => {
      alert("Subscription added!");
      setSubscriptions([...subscriptions, res.data.subscription]);
      setShowSubscriptionModal(false);
    }).catch(() => alert("Subscription failed"));
  };

  // Place an order
  const placeOrder = () => {
    if (cart.length === 0) {
      alert("Cart is empty!");
      return;
    }

    const formattedCart = cart.map(item => ({
      menuItem: item._id,
      quantity: item.quantity
    }));

    axios.post(
      "http://localhost:5000/api/orders/place",
      { restaurant: selectedRestaurant, items: formattedCart },
      { headers: { Authorization: localStorage.getItem("token") } }
    ).then((res) => {
      alert("Order placed successfully!");
      setCart([]);
      setOrders([...orders, res.data.order]);
    }).catch(() => alert("Order failed"));
  };

  // Delete an order
  const deleteOrder = (orderId) => {
    if (window.confirm("Are you sure you want to cancel this order?")) {
      axios.delete(`http://localhost:5000/api/orders/${orderId}`, 
        { headers: { Authorization: localStorage.getItem("token") } }
      ).then(() => {
        setOrders(orders.filter(order => order._id !== orderId));
        alert("Order cancelled successfully");
      }).catch(() => alert("Failed to cancel order"));
    }
  };

  // Delete a subscription
  const deleteSubscription = (subscriptionId) => {
    if (window.confirm("Are you sure you want to cancel this subscription?")) {
      axios.delete(`http://localhost:5000/api/subscriptions/${subscriptionId}`, 
        { headers: { Authorization: localStorage.getItem("token") } }
      ).then(() => {
        setSubscriptions(subscriptions.filter(sub => sub._id !== subscriptionId));
        alert("Subscription cancelled successfully");
      }).catch(() => alert("Failed to cancel subscription"));
    }
  };

  // Fetch past orders & subscriptions
  useEffect(() => {
    axios.get("http://localhost:5000/api/orders/my-orders", { headers: { Authorization: localStorage.getItem("token") } })
      .then((res) => setOrders(res.data))
      .catch(() => alert("Error fetching orders"));

    axios.get("http://localhost:5000/api/subscriptions/my-subscriptions", { headers: { Authorization: localStorage.getItem("token") } })
      .then((res) => setSubscriptions(res.data))
      .catch(() => alert("Error fetching subscriptions"));
  }, []);

  return (
    <div className="bg-gray-50 min-h-screen">
      <Navbar />
      <div className="container mx-auto px-4 py-8">
        <h1 className="text-3xl font-bold text-gray-800 mb-8">Customer Dashboard</h1>

        {!selectedRestaurant ? (
          <div className="mb-12">
            <h2 className="text-2xl font-semibold text-gray-700 mb-6">Browse Restaurants</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {restaurants.map((r) => (
                <div 
                  key={r._id} 
                  className="p-6 bg-white rounded-lg shadow-md hover:shadow-lg transition-shadow cursor-pointer border border-gray-200"
                  onClick={() => viewMenu(r._id)}
                >
                  <h3 className="text-xl font-semibold text-gray-800 mb-2">{r.name}</h3>
                  <p className="text-gray-600 mb-1"><span className="font-medium">Cuisine:</span> {r.cuisine}</p>
                  <p className="text-gray-600"><span className="font-medium">Location:</span> {r.location || "N/A"}</p>
                </div>
              ))}
            </div>
          </div>
        ) : (
          <div className="mb-12">
            <button 
              onClick={() => setSelectedRestaurant(null)} 
              className="flex items-center bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-md transition mb-6"
            >
              <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M9.707 16.707a1 1 0 01-1.414 0l-6-6a1 1 0 010-1.414l6-6a1 1 0 011.414 1.414L5.414 9H17a1 1 0 110 2H5.414l4.293 4.293a1 1 0 010 1.414z" clipRule="evenodd" />
              </svg>
              Back to Restaurants
            </button>

            <h2 className="text-2xl font-semibold text-gray-700 mb-6">Menu</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {menu.map((m) => (
                <div key={m._id} className="p-6 bg-white rounded-lg shadow-md border border-gray-200">
                  <h3 className="text-lg font-semibold text-gray-800 mb-1">{m.name}</h3>
                  <p className="text-gray-600 mb-2">{m.description}</p>
                  <p className="text-lg font-bold text-indigo-600 mb-4">{m.price}</p>
                  <div className="flex space-x-2">
                    <button 
                      onClick={() => addToCart(m)} 
                      className="flex-1 bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-md transition flex items-center justify-center"
                    >
                      <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" viewBox="0 0 20 20" fill="currentColor">
                        <path d="M3 1a1 1 0 000 2h1.22l.305 1.222a.997.997 0 00.01.042l1.358 5.43-.893.892C3.74 11.846 4.632 14 6.414 14H15a1 1 0 000-2H6.414l1-1H14a1 1 0 00.894-.553l3-6A1 1 0 0017 3H6.28l-.31-1.243A1 1 0 005 1H3zM16 16.5a1.5 1.5 0 11-3 0 1.5 1.5 0 013 0zM6.5 18a1.5 1.5 0 100-3 1.5 1.5 0 000 3z" />
                      </svg>
                      Add to Cart
                    </button>
                    <button 
                      onClick={() => openSubscriptionModal(m)} 
                      className="flex-1 bg-purple-500 hover:bg-purple-600 text-white px-4 py-2 rounded-md transition flex items-center justify-center"
                    >
                      <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" viewBox="0 0 20 20" fill="currentColor">
                        <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm1-12a1 1 0 10-2 0v4a1 1 0 00.293.707l2.828 2.829a1 1 0 101.415-1.415L11 9.586V6z" clipRule="evenodd" />
                      </svg>
                      Subscribe
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Subscription Modal */}
        {showSubscriptionModal && (
          <div className="fixed inset-0 flex items-center justify-center bg-gray-900 bg-opacity-50 z-50">
            <div className="bg-white p-8 rounded-lg shadow-xl w-full max-w-md">
              <h2 className="text-2xl font-semibold text-gray-800 mb-6">Choose Subscription Type</h2>
              
              <div className="flex space-x-4 mb-6">
                <button 
                  onClick={() => handleSubscriptionType("Monthly")} 
                  className={`flex-1 py-3 rounded-lg ${subscriptionType === "Monthly" ? "bg-green-600 text-white" : "bg-green-100 text-green-800"} font-medium transition`}
                >
                  Monthly
                </button>
                <button 
                  onClick={() => handleSubscriptionType("Weekly")} 
                  className={`flex-1 py-3 rounded-lg ${subscriptionType === "Weekly" ? "bg-yellow-600 text-white" : "bg-yellow-100 text-yellow-800"} font-medium transition`}
                >
                  Weekly
                </button>
              </div>
              
              {subscriptionType && (
                <>
                  <div className="space-y-4 mb-6">
                    <div>
                      <label className="block text-gray-700 mb-2">Start Date</label>
                      <input 
                        type="date" 
                        className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
                        onChange={(e) => setSubscriptionDates({ ...subscriptionDates, startDate: e.target.value })} 
                      />
                    </div>
                    <div>
                      <label className="block text-gray-700 mb-2">End Date</label>
                      <input 
                        type="date" 
                        className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
                        onChange={(e) => setSubscriptionDates({ ...subscriptionDates, endDate: e.target.value })} 
                      />
                    </div>
                  </div>
                  
                  <div className="flex justify-end space-x-3">
                    <button 
                      onClick={() => setShowSubscriptionModal(false)} 
                      className="px-4 py-2 bg-gray-300 hover:bg-gray-400 text-gray-800 rounded-lg transition"
                    >
                      Cancel
                    </button>
                    <button 
                      onClick={placeSubscription} 
                      className="px-4 py-2 bg-indigo-600 hover:bg-indigo-700 text-white rounded-lg transition"
                    >
                      Confirm Subscription
                    </button>
                  </div>
                </>
              )}
            </div>
          </div>
        )}

        {/* Cart Section */}
        <div className="mb-12">
          <h2 className="text-2xl font-semibold text-gray-700 mb-6">Your Cart</h2>
          {cart.length === 0 ? (
            <div className="bg-white p-6 rounded-lg shadow-md border border-gray-200 text-center">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-12 w-12 mx-auto text-gray-400 mb-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 3h2l.4 2M7 13h10l4-8H5.4M7 13L5.4 5M7 13l-2.293 2.293c-.63.63-.184 1.707.707 1.707H17m0 0a2 2 0 100 4 2 2 0 000-4zm-8 2a2 2 0 11-4 0 2 2 0 014 0z" />
              </svg>
              <p className="text-gray-600">Your cart is empty</p>
            </div>
          ) : (
            <div className="bg-white rounded-lg shadow-md overflow-hidden border border-gray-200">
              <div className="divide-y divide-gray-200">
                {cart.map((item, index) => (
                  <div key={index} className="p-4 flex justify-between items-center">
                    <div className="flex items-center space-x-4">
                      <div className="w-16 h-16 bg-gray-100 rounded-lg flex items-center justify-center">
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-8 w-8 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
                        </svg>
                      </div>
                      <div>
                        <h3 className="font-medium text-gray-800">{item.name}</h3>
                        <p className="text-gray-600">{item.price}</p>
                      </div>
                    </div>
                    <div className="flex items-center space-x-4">
                      <div className="flex items-center border border-gray-300 rounded-lg">
                        <button 
                          onClick={() => updateQuantity(index, item.quantity - 1)}
                          className="px-3 py-1 text-gray-600 hover:bg-gray-100"
                        >
                          -
                        </button>
                        <span className="px-3 py-1">{item.quantity}</span>
                        <button 
                          onClick={() => updateQuantity(index, item.quantity + 1)}
                          className="px-3 py-1 text-gray-600 hover:bg-gray-100"
                        >
                          +
                        </button>
                      </div>
                      <button 
                        onClick={() => removeFromCart(index)}
                        className="p-2 text-red-500 hover:text-red-700 hover:bg-red-50 rounded-full"
                      >
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
                        </svg>
                      </button>
                    </div>
                  </div>
                ))}
              </div>
              <div className="p-4 bg-gray-50 border-t border-gray-200">
                <div className="flex justify-between items-center mb-4">
                  <span className="font-medium text-gray-700">Subtotal</span>
                  <span className="font-bold text-gray-900">
                    {cart.reduce((total, item) => total + (item.price * item.quantity), 0)}
                  </span>
                </div>
                <button 
                  onClick={placeOrder}
                  className="w-full py-3 bg-green-600 hover:bg-green-700 text-white font-medium rounded-lg transition flex items-center justify-center"
                >
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" viewBox="0 0 20 20" fill="currentColor">
                    <path fillRule="evenodd" d="M5 9V7a5 5 0 0110 0v2a2 2 0 012 2v5a2 2 0 01-2 2H5a2 2 0 01-2-2v-5a2 2 0 012-2zm8-2v2H7V7a3 3 0 016 0z" clipRule="evenodd" />
                  </svg>
                  Place Order
                </button>
              </div>
            </div>
          )}
        </div>

        {/* My Subscriptions Section */}
        <div className="mb-12">
          <h2 className="text-2xl font-semibold text-gray-700 mb-6">My Subscriptions</h2>
          {subscriptions.length === 0 ? (
            <div className="bg-white p-6 rounded-lg shadow-md border border-gray-200 text-center">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-12 w-12 mx-auto text-gray-400 mb-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" />
              </svg>
              <p className="text-gray-600">You don't have any subscriptions yet</p>
            </div>
          ) : (
            <div className="bg-white rounded-lg shadow-md overflow-hidden border border-gray-200">
              <div className="overflow-x-auto">
                <table className="min-w-full divide-y divide-gray-200">
                  <thead className="bg-gray-50">
                    <tr>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Menu Item</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Type</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Start Date</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">End Date</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {subscriptions.map((sub) => (
                      <tr key={sub._id} className="hover:bg-gray-50">
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div className="text-sm font-medium text-gray-900">{sub.menuItem?.name || "N/A"}</div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full 
                            ${sub.subscriptionType === "Monthly" ? "bg-green-100 text-green-800" : "bg-yellow-100 text-yellow-800"}`}>
                            {sub.subscriptionType}
                          </span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                          {new Date(sub.startDate).toLocaleDateString()}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                          {new Date(sub.endDate).toLocaleDateString()}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full 
                            ${sub.status === "Active" ? "bg-blue-100 text-blue-800" : "bg-red-100 text-red-800"}`}>
                            {sub.status}
                          </span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                          <button 
                            onClick={() => deleteSubscription(sub._id)}
                            className="text-red-600 hover:text-red-900"
                          >
                            Cancel
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}
        </div>

        {/* My Orders Section */}
        <div className="mb-12">
          <h2 className="text-2xl font-semibold text-gray-700 mb-6">My Orders</h2>
          {orders.length === 0 ? (
            <div className="bg-white p-6 rounded-lg shadow-md border border-gray-200 text-center">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-12 w-12 mx-auto text-gray-400 mb-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
              </svg>
              <p className="text-gray-600">You don't have any orders yet</p>
            </div>
          ) : (
            <div className="bg-white rounded-lg shadow-md overflow-hidden border border-gray-200">
              <div className="overflow-x-auto">
                <table className="min-w-full divide-y divide-gray-200">
                  <thead className="bg-gray-50">
                    <tr>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Order #</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Restaurant</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Items</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Total</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {orders.map((order) => (
                      <tr key={order._id} className="hover:bg-gray-50">
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                          #{order._id.slice(-6).toUpperCase()}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div className="text-sm font-medium text-gray-900">{order.restaurant?.name || "N/A"}</div>
                        </td>
                        <td className="px-6 py-4">
                          <div className="text-sm text-gray-900">
                            {order.items?.map((item, idx) => (
                              <div key={idx} className="mb-1 last:mb-0">
                                <span className="font-medium">{item.menuItem?.name || "N/A"}</span> 
                                <span className="text-gray-600"> x{item.quantity}</span>
                              </div>
                            ))}
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                          {order.items?.reduce((total, item) => total + (item.menuItem?.price || 0) * item.quantity, 0)}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full 
                            ${order.status === "Delivered" ? "bg-green-100 text-green-800" : 
                              order.status === "Cancelled" ? "bg-red-100 text-red-800" : 
                              "bg-yellow-100 text-yellow-800"}`}>
                            {order.status || "Processing"}
                          </span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                          {order.status !== "Cancelled" && order.status !== "Delivered" && (
                            <button 
                              onClick={() => deleteOrder(order._id)}
                              className="text-red-600 hover:text-red-900"
                            >
                              Cancel
                            </button>
                          )}
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default CustomerDashboard;
