import React, { useState, useEffect } from "react";
import axios from "axios";
import Navbar from "../Navbar";

const SuperAdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [restaurants, setRestaurants] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/api/users", {
      headers: { Authorization: localStorage.getItem("token") },
    }).then((res) => setUsers(res.data));

    axios.get("http://localhost:5000/api/restaurants").then((res) => setRestaurants(res.data));
  }, []);

  const deleteUser = (id) => {
    axios.delete(`http://localhost:5000/api/users/${id}`, {
      headers: { Authorization: localStorage.getItem("token") },
    }).then(() => setUsers(users.filter((user) => user._id !== id)));
  };

  const deleteRestaurant = (id) => {
    axios.delete(`http://localhost:5000/api/restaurants/${id}`, {
      headers: { Authorization: localStorage.getItem("token") },
    }).then(() => setRestaurants(restaurants.filter((restaurant) => restaurant._id !== id)));
  };

  return (
    <div>
      <Navbar />
      <h2>Manage Users</h2>
      <table className="table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Role</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user._id}>
              <td>{user.name}</td>
              <td>{user.role}</td>
              <td>
                <button className="btn btn-danger" onClick={() => deleteUser(user._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>Manage Restaurants</h2>
      <table className="table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Cuisine</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {restaurants.map((restaurant) => (
            <tr key={restaurant._id}>
              <td>{restaurant.name}</td>
              <td>{restaurant.cuisine}</td>
              <td>
                <button className="btn btn-danger" onClick={() => deleteRestaurant(restaurant._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default SuperAdminDashboard;
