import React, { useState, useEffect } from "react";
import axios from "axios";
import Navbar from "../Navbar";

const CustomerDashboard = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);
  const [menu, setMenu] = useState([]);
  const [cart, setCart] = useState([]);
  const [orders, setOrders] = useState([]);
  const [loadingOrders, setLoadingOrders] = useState(false);
  const [orderError, setOrderError] = useState(null);

  // Fetch restaurants on component mount
  useEffect(() => {
    axios.get("http://localhost:5000/api/restaurants")
      .then((res) => setRestaurants(res.data))
      .catch(() => alert("Error fetching restaurants"));
  }, []);

  // Fetch orders for the logged-in customer
  useEffect(() => {
    setLoadingOrders(true);
    axios.get("http://localhost:5000/api/orders/my-orders", { 
      headers: { Authorization: localStorage.getItem("token") } 
    })
      .then((res) => {
        setOrders(res.data);
        setOrderError(null);
      })
      .catch((err) => {
        setOrderError("Error fetching orders");
        console.error("Order fetch error:", err);
      })
      .finally(() => setLoadingOrders(false));
  }, []);

  // Fetch menu for the selected restaurant
  const viewMenu = (restaurantId) => {
    axios.get(`http://localhost:5000/api/menu/${restaurantId}`)
      .then((res) => {
        setSelectedRestaurant(restaurantId);
        setMenu(res.data);
      }).catch(() => alert("Error loading menu"));
  };

  // Add item to cart
  const addToCart = (menuItem) => {
    const existingItem = cart.find((item) => item._id === menuItem._id);
    if (existingItem) {
      setCart(cart.map((item) =>
        item._id === menuItem._id ? { ...item, quantity: item.quantity + 1 } : item
      ));
    } else {
      setCart([...cart, { ...menuItem, quantity: 1 }]);
    }
  };

  // Update quantity of an item in the cart
  const updateQuantity = (itemId, quantity) => {
    if (quantity < 1) return;
    setCart(cart.map((item) =>
      item._id === itemId ? { ...item, quantity } : item
    ));
  };

  // Remove item from cart
  const removeFromCart = (itemId) => {
    setCart(cart.filter((item) => item._id !== itemId));
  };

  // Place order
  const placeOrder = () => {
    if (!selectedRestaurant || cart.length === 0) return;
    
    axios.post(
      "http://localhost:5000/api/orders/place",
      { restaurant: selectedRestaurant, items: cart },
      { headers: { Authorization: localStorage.getItem("token") } }
    ).then((res) => {
      alert("Order placed successfully!");
      setCart([]);
      setOrders([res.data.order, ...orders]); // Add new order at beginning
    }).catch((err) => {
      alert("Order failed. Please try again.");
      console.error("Order error:", err);
    });
  };

  // Calculate total price of items in the cart
  const totalPrice = cart.reduce((total, item) => total + item.price * item.quantity, 0);

  return (
    <div className="bg-gray-100 min-h-screen">
      <Navbar />
      <div className="container mx-auto p-6">
        <h1 className="text-3xl font-bold text-gray-800 mb-6">Customer Dashboard</h1>

        {!selectedRestaurant ? (
          <>
            <h2 className="text-2xl font-semibold text-gray-700 mb-4">Browse Restaurants</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {restaurants.map((r) => (
                <div
                  key={r._id}
                  className="p-4 bg-white shadow rounded-lg cursor-pointer hover:shadow-md transition"
                  onClick={() => viewMenu(r._id)}
                >
                  <h3 className="text-xl font-semibold text-gray-800">{r.name}</h3>
                  <p className="text-gray-600">{r.cuisine}</p>
                </div>
              ))}
            </div>
          </>
        ) : (
          <>
            <button 
              onClick={() => setSelectedRestaurant(null)}
              className="mb-4 flex items-center text-blue-600 hover:text-blue-800"
            >
              <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-1" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M9.707 16.707a1 1 0 01-1.414 0l-6-6a1 1 0 010-1.414l6-6a1 1 0 011.414 1.414L5.414 9H17a1 1 0 110 2H5.414l4.293 4.293a1 1 0 010 1.414z" clipRule="evenodd" />
              </svg>
              Back to Restaurants
            </button>

            <h2 className="text-2xl font-semibold text-gray-700 mb-4">Menu</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {menu.map((m) => (
                <div key={m._id} className="p-4 bg-white shadow rounded-lg">
                  <h3 className="text-lg font-semibold text-gray-800">{m.name}</h3>
                  <p className="text-gray-600">{m.description}</p>
                  <p className="text-gray-800 font-bold">₹{m.price}</p>
                  <button
                    onClick={() => addToCart(m)}
                    className="bg-blue-500 text-white px-4 py-2 mt-2 rounded-md hover:bg-blue-700 transition"
                  >
                    Add to Cart
                  </button>
                </div>
              ))}
            </div>

            <h2 className="text-2xl font-semibold text-gray-700 mt-6">Cart</h2>
            {cart.length === 0 ? (
              <p className="text-gray-600">No items in cart</p>
            ) : (
              <div className="p-4 bg-white shadow rounded-lg">
                {cart.map((item) => (
                  <div key={item._id} className="flex justify-between items-center mb-2">
                    <div>
                      <p className="text-gray-800 font-semibold">{item.name}</p>
                      <p className="text-gray-600">₹{item.price} x {item.quantity}</p>
                    </div>
                    <div className="flex items-center gap-2">
                      <button
                        onClick={() => updateQuantity(item._id, item.quantity - 1)}
                        className="bg-gray-200 text-gray-800 px-2 py-1 rounded-md hover:bg-gray-300 transition"
                      >
                        -
                      </button>
                      <span>{item.quantity}</span>
                      <button
                        onClick={() => updateQuantity(item._id, item.quantity + 1)}
                        className="bg-gray-200 text-gray-800 px-2 py-1 rounded-md hover:bg-gray-300 transition"
                      >
                        +
                      </button>
                      <button
                        onClick={() => removeFromCart(item._id)}
                        className="bg-red-500 text-white px-2 py-1 rounded-md hover:bg-red-600 transition"
                      >
                        Remove
                      </button>
                    </div>
                  </div>
                ))}
                <p className="text-xl font-semibold text-gray-800 mt-4">Total: ₹{totalPrice.toFixed(2)}</p>
                <button
                  onClick={placeOrder}
                  disabled={cart.length === 0}
                  className={`px-4 py-2 mt-4 rounded-md transition ${
                    cart.length === 0 
                      ? 'bg-gray-400 cursor-not-allowed' 
                      : 'bg-green-500 hover:bg-green-700 text-white'
                  }`}
                >
                  Place Order
                </button>
              </div>
            )}
          </>
        )}

        <h2 className="text-2xl font-semibold text-gray-700 mt-6">My Orders</h2>
        
        {loadingOrders ? (
          <div className="text-center py-8">
            <p>Loading your orders...</p>
          </div>
        ) : orderError ? (
          <div className="text-center py-8 text-red-500">
            <p>Error loading orders. Please try again later.</p>
          </div>
        ) : orders.length === 0 ? (
          <div className="bg-white rounded-lg shadow p-6 text-center">
            <p className="text-gray-600">You haven't placed any orders yet.</p>
          </div>
        ) : (
          <div className="space-y-6">
            {orders.map((order) => {
              const total = order.items.reduce((sum, item) => 
                sum + ((item.menuItem?.price || 0) * (item.quantity || 0)), 0);
              
              return (
                <div key={order._id} className="bg-white rounded-lg shadow overflow-hidden">
                  <div className="p-4 border-b">
                    <div className="flex justify-between items-center">
                      <h3 className="font-medium text-lg">
                        {order.restaurant?.name || "Unknown Restaurant"}
                        {!order.restaurant?._id && (
                          <span className="text-red-500 text-xs ml-2">(Restaurant no longer exists)</span>
                        )}
                      </h3>
                      <span className={`px-3 py-1 rounded-full text-sm ${
                        order.status.toLowerCase().includes('cancel') ? 'bg-red-100 text-red-800' :
                        order.status.toLowerCase().includes('deliver') ? 'bg-green-100 text-green-800' :
                        'bg-blue-100 text-blue-800'
                      }`}>
                        {order.status}
                      </span>
                    </div>
                    <p className="text-sm text-gray-500">
                      Ordered on: {new Date(order.timestamp).toLocaleString()}
                    </p>
                  </div>
                  
                  <div className="p-4">
                    <h4 className="font-medium mb-2">Order Items:</h4>
                    <ul className="divide-y">
                      {order.items.map((item, index) => {
                        const itemTotal = (item.menuItem?.price || 0) * (item.quantity || 0);
                        return (
                          <li key={item.menuItem?._id || index} className="py-2 flex justify-between">
                            <div>
                              <span>{item.menuItem?.name || "Unavailable Item"}</span>
                              {!item.menuItem?._id && (
                                <span className="text-red-500 text-xs ml-2">(Item no longer available)</span>
                              )}
                              <span className="text-gray-500 text-sm block">× {item.quantity || 0}</span>
                            </div>
                            <div className="font-medium">
                              ₹{itemTotal.toFixed(2)}
                            </div>
                          </li>
                        );
                      })}
                    </ul>
                  </div>
                  
                  <div className="p-4 bg-gray-50 border-t flex justify-between items-center">
                    <span className="font-medium">Total:</span>
                    <span className="font-bold">₹{total.toFixed(2)}</span>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>
    </div>
  );
};

export default CustomerDashboard;
