import React, { useState, useEffect } from "react";
import axios from "axios";
import Navbar from "../Navbar";

const RestaurantDashboard = () => {
  const [orders, setOrders] = useState([]);
  const [restaurantData, setRestaurantData] = useState({ name: "", address: "", cuisine: "" });
  const [restaurants, setRestaurants] = useState([]);
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);
  const [menuItems, setMenuItems] = useState([]);
  const [menuData, setMenuData] = useState({ name: "", description: "", price: "" });

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/orders/restaurant-orders", { headers: { Authorization: localStorage.getItem("token") } })
      .then((res) => setOrders(res.data))
      .catch(() => alert("Error fetching orders"));

    axios
      .get("http://localhost:5000/api/restaurants/my-restaurants", { headers: { Authorization: localStorage.getItem("token") } })
      .then((res) => setRestaurants(res.data))
      .catch(() => alert("Error fetching your restaurants"));
  }, []);

  const handleChange = (e) => {
    setRestaurantData({ ...restaurantData, [e.target.name]: e.target.value });
  };

  const addRestaurant = (e) => {
    e.preventDefault();
    axios.post(
      "http://localhost:5000/api/restaurants/add",
      restaurantData,
      { headers: { Authorization: localStorage.getItem("token") } }
    ).then((res) => {
      alert(res.data.message);
      setRestaurants([...restaurants, res.data.restaurant]);
      setRestaurantData({ name: "", address: "", cuisine: "" });
    }).catch(() => alert("Error adding restaurant"));
  };

  const viewMenu = (restaurantId) => {
    setSelectedRestaurant(restaurantId);
    axios.get(`http://localhost:5000/api/restaurants/${restaurantId}/menu`)
      .then((res) => setMenuItems(res.data))
      .catch(() => alert("Error fetching menu items"));
  };

  const handleMenuChange = (e) => {
    setMenuData({ ...menuData, [e.target.name]: e.target.value });
  };

  const addMenuItem = (e) => {
    e.preventDefault();
    axios.post(
      "http://localhost:5000/api/restaurants/add-menu",
      { ...menuData, restaurantId: selectedRestaurant },
      { headers: { Authorization: localStorage.getItem("token") } }
    ).then((res) => {
      alert(res.data.message);
      setMenuItems([...menuItems, res.data.menuItem]);
      setMenuData({ name: "", description: "", price: "" });
    }).catch(() => alert("Error adding menu item"));
  };

  return (
    <div className="bg-gray-100 min-h-screen">
      <Navbar />
      <div className="container mx-auto p-6">
        <h1 className="text-3xl font-bold text-gray-800 mb-6">Restaurant Admin Dashboard</h1>

        {/* Add New Restaurant Form */}
        <h2 className="text-2xl font-semibold text-gray-700">Add a New Restaurant</h2>
        <form onSubmit={addRestaurant} className="bg-white p-6 rounded-lg shadow-md space-y-4">
          <input type="text" name="name" placeholder="Restaurant Name" value={restaurantData.name} onChange={handleChange} required 
            className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <input type="text" name="address" placeholder="Address" value={restaurantData.address} onChange={handleChange} required 
            className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <input type="text" name="cuisine" placeholder="Cuisine Type" value={restaurantData.cuisine} onChange={handleChange} required 
            className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-700 transition">
            Add Restaurant
          </button>
        </form>

        {/* Show Restaurants */}
        <h2 className="text-2xl font-semibold text-gray-700 mt-6">Your Restaurants</h2>
        {restaurants.map((r) => (
          <div key={r._id} className="p-4 bg-white shadow rounded-lg cursor-pointer hover:shadow-md" onClick={() => viewMenu(r._id)}>
            <h3 className="text-xl font-semibold">{r.name}</h3>
            <p className="text-gray-600">{r.cuisine}</p>
          </div>
        ))}

        {/* Add Menu & Show Menu Items */}
        {selectedRestaurant && (
          <>
            <h2 className="text-2xl font-semibold text-gray-700 mt-6">Add Menu Item</h2>
            <form onSubmit={addMenuItem} className="bg-white p-6 rounded-lg shadow-md space-y-4">
              <input type="text" name="name" placeholder="Menu Item Name" value={menuData.name} onChange={handleMenuChange} required className="w-full p-3 border rounded-lg"/>
              <input type="text" name="description" placeholder="Description" value={menuData.description} onChange={handleMenuChange} className="w-full p-3 border rounded-lg"/>
              <input type="number" name="price" placeholder="Price" value={menuData.price} onChange={handleMenuChange} required className="w-full p-3 border rounded-lg"/>
              <button type="submit" className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-700">Add Menu Item</button>
            </form>
            <h2 className="text-2xl font-semibold text-gray-700 mt-6">Menu Items</h2>
            {menuItems.map((item) => <p key={item._id}>{item.name} - â‚¹{item.price}</p>)}
          </>
        )}
      </div>
    </div>
  );
};

export default RestaurantDashboard;
